mapper.select:
select *, ROW_NUMBER() OVER (ORDER BY priority asc, ((259200/TIME_TO_SEC(TIMEDIFF(NOW(),createDate)))+(likedCount+1)) DESC) as id from (select R.id as reviewId, U.username as username, U.id as userId, content as content, S.name as storeName, S.id as storeId, S.category as category, S.address as address, R.rating as rating, R.create_date as createDate, if(B.id, 'y', 'n') as isBookmarked, if(L.id, 'y', 'n') as isLiked, R.liked_count as likedCount, 1 as priority from REVIEWS R join USERS U on R.customer_id = U.id join STORES S on R.store_id = S.id join FOLLOWS F on R.customer_id = F.followed_id and #{userId} = F.follower_id left join BOOKMARKS B on S.id = B.store_id and B.customer_id = #{userId} left join REVIEW_LIKES L on R.id = L.review_id and #{userId} = L.customer_id
union
select R.id as reviewId, U.username as username, U.id as userId, R.content as content, S.name as storeName, S.id as storeId, S.category as category, S.address as address, R.rating as rating, R.create_date as createDate, if(B.id, 'y', 'n') as isBookmarked, if(L.id, 'y', 'n') as isLiked, R.liked_count as likedCount, 2 as priority from REVIEWS R join USERS U on R.customer_id = U.id join STORES S on R.store_id = S.id left join BOOKMARKS B on S.id = B.store_id and B.customer_id = #{userId} left join REVIEW_LIKES L on R.id = L.review_id and #{userId} = L.customer_id ) subquery limit #{begin}, #{limit}

select *, ROW_NUMBER() OVER (ORDER BY ((259200/TIME_TO_SEC(TIMEDIFF(NOW(),createDate)))+(likedCount+1)) DESC) as id from (select R.id as reviewId, U.username as username, U.id as userId, R.content as content, S.name as storeName, S.id as storeId, S.category as category, S.address as address, R.rating as rating, R.create_date as createDate, R.liked_count as likedCount
from REVIEWS R join USERS U on R.customer_id = U.id join STORES S on R.store_id = S.id
) as subquery limit #{begin}, #{limit}
